import datetime


def _get_duration_components(duration):
    day = duration.days
    second = duration.seconds
    microsecond = duration.microseconds

    minutes = second // 60
    second %= 60

    hours = minutes // 60
    minutes %= 60

    return day, hours, minutes, second, microsecond


def duration_string(duration):
    """Version of str(timedelta) which is not English specific."""
    day, hours, minutes, second, microsecond = _get_duration_components(duration)

    string = "{:02d}:{:02d}:{:02d}".format(hours, minutes, second)
    if day:
        string = "{} ".format(day) + string
    if microsecond:
        string += ".{:06d}".format(microsecond)

    return string


def duration_iso_string(duration):
    if duration < datetime.timedelta(0):
        sign = "-"
        duration *= -1
    else:
        sign = ""

    days, hours, minutes, seconds, microsecond = _get_duration_components(duration)
    ms = ".{:06d}".format(microsecond) if microsecond else ""
    return "{}P{}DT{:02d}H{:02d}M{:02d}{}S".format(
        sign, days, hours, minutes, seconds, ms
    )


def duration_microseconds(delta):
    return (24 * 60 * 60 * delta.days + delta.seconds) * 1000000 + delta.microseconds
